beforeSaveCustomScript:
// Calculate amount of product produced
$productIdsFromFormula = list();
$ingredientsFromFormula = object\create();
$ingredientsFromInput = object\create();

$productionLog = record\fetch('CProductionLog', cProductionLogId);
$formulaId = object\get($productionLog, 'cProductFormulaId');
$formula = record\fetch('CProductFormula', $formulaId);
$productionLogJson = json\encode($productionLog);

// Get expected ingredients and amount from formula
$i = 0;
$formulaListIds = object\get($formula, 'cProductFormulaListsIds');
while ($i < array\length($formulaListIds)) {
    $id = array\at($formulaListIds, $i);
    $formulaList = record\fetch('CProductFormulaList', $id);
    $productId = object\get($formulaList, 'productId');
    $productAmount = object\get($formulaList, 'quantity');
    $productIdsFromFormula = array\push($productIdsFromFormula, $productId);
    object\set($ingredientsFromFormula, $productId, $productAmount);
    $i = $i + 1;
}

// Get ingredients and amount from production log
$i = 0;
$ingredientsAmountIds = object\get($productionLog, 'cIngredientsAmountsIds');
while ($i < array\length($ingredientsAmountIds)) {
    $id = array\at($ingredientsAmountIds, $i);
    $ingredientAmount = record\fetch('CIngredientsAmount', $id);
    $productId = object\get($ingredientAmount, 'productId');
    $productAmount = object\get($ingredientAmount, 'amount');
    object\set($ingredientsFromInput, $productId, $productAmount);
    $i = $i + 1;
}

object\set($ingredientsFromInput, productId, amount);

$isInputComplete = true;
$producedAmount = 999999;

$i = 0;
while ($i < array\length($productIdsFromFormula)) {
    $productId = array\at($productIdsFromFormula, $i);
    if (!object\has($ingredientsFromInput, $productId)) {
        $isInputComplete = false;
        break;
    }
    $amountFromFormula = object\get($ingredientsFromFormula, $productId);
    $amountFromInput = object\get($ingredientsFromInput, $productId);
    $currentAmountProduced = number\floor($amountFromInput/$amountFromFormula);
    if ($currentAmountProduced < $producedAmount) {
        $producedAmount = $currentAmountProduced;
    }
    $i = $i + 1;
}

if ($isInputComplete) {
    if (object\get($productionLog, 'productionStatus') == 'Failed') {
        $totalRecycledProduct = object\get($productionLog, 'totalRecycledProduct');
        $producedAmount = $producedAmount - $totalRecycledProduct;
    }
} else {
    $producedAmount = 0;
}

record\update('CProductionLog', cProductionLogId, 'total', $producedAmount);
