beforeSaveCustomScript:
name=product.name;

$productionLog = record\fetch('CProductionLog', cProductionLogId);

$amount = amount;
$warehouseId = object\get($productionLog, 'warehouseId');

$status = entity\isNew();
if ($status) {
    record\create('InventoryTransaction',
        'type', 'Transfer',
        'quantity', -$amount,
        'productId', productId,
        'warehouseId', $warehouseId,
        'productionLogId', cProductionLogId,
    );
} else {
    $inventoryTransactionId = record\findOne('InventoryTransaction', 'createdAt', 'desc', 'productionLogId=', cProductionLogId, 'productId=', productId);

    record\update('InventoryTransaction', $inventoryTransactionId, 'quantity', $amount);
}
